function initCheckForm() {
	var animSpeed = 400;
	var visibleClass = 'row-visible';

	jQuery('.coustum-form').each(function(){
		var form = jQuery(this);

		var checkBoxes = form.find('.checkbox-list :checkbox');
		var noSelect = form.find('.no-slec');
		var dataRows = form.find('.member-hold .iner-row');
		var submitBtn = form.find('.submit-btn');

		var emptyFlag = true;

		checkBoxes.each(function(){
			var checkBox = jQuery(this);
			var id = checkBox.attr('id');

			if (checkBox.is(':checked')) {
				form.addClass(visibleClass);
				emptyFlag = false;
			}

			checkBox.on('change', function() {
				if (checkBox.is(':checked')) {
					showRow(id);
				} else {
					hideRow(id);
				}
			});
		});

		if (emptyFlag) {
			dataRows.add(submitBtn).hide();
			noSelect.show();
		}

		function showRow(name) {
			var curRow = dataRows.filter('[data-id="' + name + '"]');

			if (!form.hasClass(visibleClass)) {
				form.addClass(visibleClass);

				noSelect.stop().fadeOut({
					duration: animSpeed,
					complete: function(){
						curRow.add(submitBtn).stop().fadeIn(animSpeed);
					}
				});
			} else {
				curRow.fadeIn(animSpeed);
			}
		}

		function hideRow(name) {
			var curRow = dataRows.filter('[data-id="' + name + '"]');
			var activeCheck = checkBoxes.filter(':checked');

			if (activeCheck.length === 0) {
				form.removeClass(visibleClass);

				curRow.add(submitBtn).stop().fadeOut({
					duration: animSpeed,
					complete: function(){
						noSelect.fadeIn(animSpeed);
					}
				});
			} else {
				curRow.stop().fadeOut(animSpeed);
			}
		}
	});
}

